import React, { useState } from "react";
import {
  StyleSheet,
  View,
  Keyboard,
  TouchableWithoutFeedback,
  AsyncStorage,
} from "react-native";

import { primaryColors, fonts } from "../models/Styles.js";

import PriceArea from "./PriceArea.js";
import DateHeader from "./DateHeader.js";
import ChartPanel from "./ChartPanel.js";
import PageLink from "./PageLink.js";

const DismissKeyboard = ({ children }) => (
  <TouchableWithoutFeedback onPress={() => Keyboard.dismiss()}>
    {children}
  </TouchableWithoutFeedback>
);

const Home = () => {
  const [panelIsOpen, setPanel] = useState(false);
  const setPanelState = (panelState) => {
    setPanel(panelState);
  };
  if (!panelIsOpen) {
    return (
      <DismissKeyboard>
        <View style={styles.container}>
          <DateHeader
            style={{
              color: "rgba(0,0,0,1)",
            }}
          />
          <PriceArea />
          <ChartPanel setPanelState={setPanelState} panelIsOpen={panelIsOpen} />
          <View
            style={{
              flex: 1,
              flexDirection: "row",
              justifyContent: "space-between",
              marginTop: 2,
            }}
          >
            <PageLink link="Settings" />
            <PageLink link="Help" />
            <PageLink link="Login" />
          </View>
        </View>
      </DismissKeyboard>
    );
  } else {
    return (
      <DismissKeyboard>
        <View style={styles.container}>
          <DateHeader
            style={{
              color: "rgba(0,0,0,0.5)",
            }}
          />
          <PriceArea />
          <ChartPanel setPanelState={setPanelState} panelIsOpen={panelIsOpen} />
        </View>
      </DismissKeyboard>
    );
  }
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: primaryColors.islandgreen,
    alignItems: "center",
    paddingTop: 50,
  },
});

export default Home;
