import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  Text,
  View,
  TouchableWithoutFeedback,
  AsyncStorage,
} from "react-native";

import { primaryColors, fonts } from "../models/Styles.js";

const ChartPanel = ({ setPanelState, panelIsOpen }) => {
  const togglePanel = () => {
    setPanelState(!panelIsOpen);
  };

  if (panelIsOpen) {
    return (
      <View>
        <TouchableWithoutFeedback onPress={togglePanel}>
          <View style={styles.openPanel}>
            <Text>Display Price</Text>
          </View>
        </TouchableWithoutFeedback>
      </View>
    );
  } else {
    return (
      <View>
        <TouchableWithoutFeedback onPress={togglePanel}>
          <View style={styles.closedPanel}></View>
        </TouchableWithoutFeedback>
      </View>
    );
  }
};

const styles = StyleSheet.create({
  closedPanel: {
    backgroundColor: primaryColors.cream,
    height: 380,
    width: 380,
    borderRadius: 10,
    marginTop: 5,
  },
  openPanel: {
    backgroundColor: primaryColors.cream,
    height: 600,
    width: 380,
    borderRadius: 10,
    position: "absolute",
    marginLeft: -190,
    marginTop: -150,
    zIndex: 1,
  },
});

export default ChartPanel;
